
document.writeln("<style type=\"text/css\">");
document.writeln(".swtdiv1{");
document.writeln("	display: none;");
document.writeln("	width: 440px;");
document.writeln("	height: 249px;");
document.writeln("	position: fixed;");
document.writeln("	top: 0;");
document.writeln("	right: 0;");
document.writeln("	bottom: 0;");
document.writeln("	left: 0;");
document.writeln("	margin: auto;");
document.writeln("	z-index:9999;");
document.writeln("	-webkit-filter: drop-shadow(0px 0px 9px rgba(0,0,0,0.8));");
document.writeln("	-o-filter: drop-shadow(0px 0px 9px rgba(0,0,0,0.8));");
document.writeln("	filter: drop-shadow(0px 0px 9px rgba(0,0,0,0.8));");
document.writeln("}");
document.writeln(".swtdiv2{");
document.writeln("	width: 440px;");
document.writeln("	height: 249px;");
document.writeln("	background: url(/static/images/swtpinpaiimg1.jpg) center no-repeat;");
document.writeln("	position: relative;");
document.writeln("}");
document.writeln(".pcswtclose{");
document.writeln("    position: absolute;");
document.writeln("    right: 0;");
document.writeln("    top: 0;");
document.writeln("	  transition: 0.5s;");
document.writeln("	  width: 38px;");
document.writeln("}");
document.writeln(".swtzhuan{");
document.writeln("	-webkit-transform: rotate(359deg);");
document.writeln("	-ms-transform: rotate(359deg);");
document.writeln("	-o-transform: rotate(359deg);");
document.writeln("	transform: rotate(359deg);");
document.writeln("}");
document.writeln(".bianhua{");
document.writeln("	-webkit-animation: lineA 0.8s linear forwards;");
document.writeln("	-o-animation: lineA 0.8s linear forwards;");
document.writeln("	animation: lineA 0.8s linear forwards;");
document.writeln("}");
document.writeln("@keyframes lineA{");
document.writeln("	0%{clip:rect(0,440px,249px,0)}");
document.writeln("	100%{clip:rect(125px,220px,125px,220px)}");
document.writeln("}");
document.writeln(".pcswtclick{");
document.writeln("	width: 100%;");
document.writeln("	height: 100%;");
document.writeln("	display: block;");
document.writeln("}");
document.writeln("</style>");
document.writeln("<div class=\"swtdiv\">");
document.writeln("<div class=\"swtdiv1\">");
document.writeln("	<div class=\"swtdiv2\">	");
document.writeln("	<a href=\"javascript:void(0);\" onclick=\"openZoosUrl();\" class=\"pcswtclick\"></a>");
document.writeln("	<img src=\"images/swt_colse.png\" class=\"pcswtclose\">");
document.writeln("	</div>");
document.writeln("</div>");
document.writeln("</div>");


document.writeln("<script type=\"text/javascript\">");
document.writeln("	$(function () {");
document.writeln("		$(\'.swtdiv1\').delay(6000).fadeIn(400)");
document.writeln("		$(\'.pcswtclose\').hover(function() {");
document.writeln("			$(this).addClass(\'swtzhuan\')");
document.writeln("		}, function() {");
document.writeln("			$(this).removeClass(\'swtzhuan\')");
document.writeln("		});");
document.writeln("		$(\'.pcswtclose\').click(function() {");
document.writeln("			$(\'.swtdiv1\').addClass(\'bianhua\');");
document.writeln("			$(\'.swtdiv1\').delay(1200).hide(100)");
document.writeln("			setTimeout(function () {");
document.writeln("				$(\'.swtdiv1\').removeClass(\'bianhua\').fadeIn(400)");
document.writeln("			},20000)");
document.writeln("		});");
document.writeln("	})");
document.writeln("</script>");